import React, { useState, useEffect } from 'react'
import FetchCovidFinland from '../api/FetchCovidFinland';
import { createContainer } from "unstated-next";
import moment from 'moment';

const headers = new Headers({
  "Accept": "application/json",
  "Content-Type": "application/json",
  "User-Agent": "Mozilla/5.0 (Macintosh; Intel Mac OS X 10.15; rv:78.0) Gecko/20100101 Firefox/78.0"
});

export default function Covid() {
  const [covid, setCovid] = useState<any>("")
  const [data, setData] = useState<any>([])

  function countryDataGraph(data: any, country: string, caseType: string, dateTo: string) {
    let resultCases: any[] = []
    let resultDates: any[] = []
    if (data.length > 0) {
      data.forEach((item: any) => {

          if ((moment(item.Date).isAfter(dateTo))) {
            resultCases.push(item.Cases);
            resultDates.push(moment(item.Date).format('YYYY/MM/DD'));
        }
      })
      let resultCasesDiff: any[] = _.map(resultCases, function (e: any, i: any) {
        return (resultCases[i + 1] - e) < 0 ? 0 : (resultCases[i + 1] - e)
      });
      resultCasesDiff.pop();
      resultCases.pop();
      resultDates.pop();
      dataDiff === [] ? console.log(dataDiff) : console.log('');
      setData(resultCasesDiff);// inversion between resultCasesDiff and resultCases for display graph of variations and not total
      setDataDiff(resultCases);
      setLabels(resultDates);
      return ({ resultCases, resultDates, resultCasesDiff });
    } else {
      setData([]);
      setLabels([]);
      setDataDiff([]);

    }
  }

  const getData = async () => {
    try {
      // const response = await fetch("https://sampo.thl.fi/pivot/prod/en/epirapo/covid19case/fact_epirapo_covid19case.json", {
      const country: string = 'finland';
      const caseType: string = 'confirmed';
      const dateTo: string = `${moment().subtract(3, 'months').format()}`
      await fetch(`https://api.covid19api.com/country/${country}/status/${caseType}/live?from=${dateTo}&to=${moment().format('YYYY-MM-DDT00:00:00')}`)
        .then(res => res.json())
        .then(resp => setCovid(resp))
        .catch((err) => { throw (err) })
      console.log("getData ", covid);
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    getData();
  }, [])

  useEffect(() => {
    console.log("Covid : ", covid)
  }, [covid])

  return (
    <div className="main-container">
      {
        covid && (
          <div className="city">
            <div className="city-temp">
              Today
            </div>
            <div className="info">
              <p style={{ color: "black" }}>{covid.label}</p>
            </div>
          </div>
        )
      }
    </div>
  )
}
