import React, { useEffect, useState } from 'react';
import FetchEventsTurku from '../api/FetchEventsTurku';
import { StoreContainer } from '../Store';
import parse from 'html-react-parser';



export default function ListImages() {
  const unstated = StoreContainer.useContainer();
  const [allEvents, setAllEvents] = useState<any>(unstated.events)
  const [query, setQuery] = useState<string>("")
  const [url, setUrl] = useState<string>("https://api.turku.fi/linkedevents/v1/event/?page=1")
  const [page, setPage] = useState<string>("")

  const getAllEvents = async (url: any) => {
    const res: any = await FetchEventsTurku(url);
    // console.log(res.status);
    // console.log(res.data);
    // console.log(res.data.data);
    setAllEvents(res.data.data);
  }

  // const search = async (e: any) => {
  //   if (e.key === "Enter") {
  //     // const res: any = await FetchData(query);
  //     // console.log(query);
  //     setQuery('')
  //   }
  // }

  useEffect(() => {
    if (!unstated.events)
      unstated.fetchEventsUrl(url);
  }, [])

  const parseHtml = (htmlString: any) => {
    let res: any;
    res = htmlString.replace('<p>','')
    // return (<div dangerouslySetInnerHTML={{ __html: htmlString }} />);
  }

  return (
    <div className="content">
      {/* <div style={{ backgroundImage: "url(" + "/images/turku.png" + ")", backgroundPosition: 'center' }}> */}
      <div>
        <h1 className="text-center">Events in TURKU</h1>
        <div className="input-group-prepend mb-3 pl-2 pr-2">
          <input type="text" className="form-control border search" placeholder="Search a place..." value={query} onChange={(e) => setQuery(e.target.value)} aria-label="search place" aria-describedby="alcohol" />
        </div>
      </div>
      <div className="d-flex row">
        {allEvents &&
          allEvents.map((event: any, i: number) =>
            (<div key={i} className="col-lg-4 col-md-6 col-sm-6 ">
              <div className="card clearfix" >
                {event.images.length > 0 ?
                  <img className="card-img-top img-fluid " src={event.images[0].url} alt="event" style={{ width: "100%", height: "15vw", objectFit: 'cover' }} />
                  : <img className="card-img-top img-fluid " src='/images/logo.png' alt="nothing" style={{ width: "100%", height: "15vw", objectFit: 'cover' }} />}
                <div className="card-block">
                  <div className="card-body">
                    <h5 className="card-title">{event.name ? event.name.fi : 'no name'}</h5>
                    <button type="button" className="btn btn-info btn-sm" data-toggle="collapse" data-target={`#detail_event${i}`} >Details</button>
                    <div id={`detail_event${i}`} className="collapse card-text">
                      {event.description ? parseHtml(event.description.fi) : ''}
                    </div>
                  </div>
                </div>
              </div>
            </div >)
          )
        }
      </div>
    </div >
  )
}


