import React, { useState, useEffect } from 'react'
import { readRemoteFile } from 'react-papaparse'

export default function TableCsvName() {
  const [header, setHeader] = useState<any>([]);
  const [rows, setRows] = useState<any>([]);
  const [query, setQuery] = useState<string>("")
  const [order, setOrder] = useState<number>(1)
  const [loading, setLoading] = useState<boolean>(true)


  // {/**
  //   filter a key in the array, return true if find it in any row of array then return tue object array filtered
  // */}
  function filterData() {
    return !query
      ? rows :
      rows.filter((obj: any) => {
        let flag: Boolean = false;
        Object.values(obj).forEach((val: any) => {
          if (String(val ? val.toLowerCase() : val).indexOf(query.toLowerCase()) > -1) {
            flag = true;
            return null;
          }
        });
        if (flag) return obj;
        return null;
      });
  }

  // {/**
  //   function : sort a column giving the key name
  // */}
  const sorting = (key: string) => {
    setOrder(order * -1);
    setRows(rows.sort((a: any, b: any) =>
      (order === 1 ? a[key] > b[key] : a[key] < b[key]) ? 1 : -1
    ));
  }

  const getNamesData = (name: any) => {
    setLoading(true);
    readRemoteFile(name, {
      complete: (results: any) => {
        if (results) {
          setHeader(results.data.shift());
          setRows(results.data);
        }
      }
    })
    setLoading(false);
  }

  useEffect(() => {
    getNamesData('/images/first_name.csv');
  }, [])

  useEffect(() => {
    setLoading(false);
  }, [rows])


  return (
    <div className="content">
      {loading ? (<div className="col-12 text-center"><img height='50' width='50' src='/images/loading.gif' alt='LOADING' />LOADING</div>) : ""}
      <div className="card">
        <div className="card-header">
          <h1 className=" card-title text-center pt-1">Popular first names / family names </h1>
        </div>
        <div className="d-flex mt-2 mb-2 justify-content-center">
          <span className="badge badge-pill badge-secondary mr-1 ml-1" onClick={() => getNamesData('/images/first_name.csv')}>name</span>
          <span className="badge badge-pill badge-secondary mr-1" onClick={() => getNamesData('/images/year_name.csv')}>year</span>
          <span className="badge badge-pill badge-secondary mr-1" onClick={() => getNamesData('/images/family_name.csv')}>family</span>
          <span className="badge badge-pill badge-secondary mr-1" onClick={() => getNamesData('/images/location_name.csv')}>location</span>
        </div>
        <div className="input-group-prepend pb-0 pl-2 pr-2">
          <input type="text" className="form-control border search" placeholder="Search ..." value={query} onChange={(e) => setQuery(e.target.value)} aria-label="search place" aria-describedby="alcohol" />
        </div>
        <div className="card-body">
          <div className="table-responsive">
            <table className="table tablesorter " id="">
              <thead className=" text-primary">
                <tr>
                  {header && (header.map((name: string, ii: number) => (
                    <th key={ii} onClick={() => sorting(name)} >{name}&#8597;</th>)
                  ))}
                </tr>
              </thead>
              <tbody>
                {filterData() &&
                  (filterData().map((numList: any, i: number) => (
                    <tr key={i} >
                      {
                        numList.map((num: any, j: number) =>
                          <td key={j}>{num}</td>)
                      }
                    </tr>)
                  ))
                }
              </tbody>
            </table>
          </div>
        </div>
      </div >
    </div >
  )
}
