import React from 'react';
// import { Line } from 'react-chartjs-2';
import { Bar } from 'react-chartjs-2';

export default function LinearChart(props: any) {
  const data = {
    labels: props.labels,
    datasets: [
      {
        label: `COVID-19 confirmed cases - last 3 months`,
        data: props.data,
        backgroundColor: "red",
        color: "rgba(255,0,255,0,75)",
        hoverBackgroundColor: "rgb(255,255,153,0.8)",
      }
    ]
  }
  const options = {
    legend: {
      display: true,
      fontColor: 'whitesmoke'
    },
    title: {
      display: true,
      text: `Finland - covid19`,
      fontColor: 'whitesmoke',
    },
    scales: {
      yAxes: [{
        ticks: {
          fontColor: "white",
          fontSize: 18,
          color: "rgba(2, 2, 2, 0.1)"
        }
      }],
      xAxes: [{
        ticks: {
          fontColor: "white",
          fontSize: 14,
        }
      }]
    }
  }
  return (
    <div style={{ backgroundColor: "#555555", width:'10%' }}>
      <Bar data={data} options={options} />
    </div>
  );
}
