import React, { useState, useEffect } from 'react'
import FetchCovidFinland from '../api/FetchCovidFinland';
import { createContainer } from "unstated-next";
import moment from 'moment';

const headers = new Headers({
  "Accept": "application/json",
  "Content-Type": "application/json",
  "User-Agent": "Mozilla/5.0 (Macintosh; Intel Mac OS X 10.15; rv:78.0) Gecko/20100101 Firefox/78.0"
});

export default function Covid() {
  const [covid, setCovid] = useState<any>("")

  const getData = async () => {
    try {
      // const response = await fetch("https://sampo.thl.fi/pivot/prod/en/epirapo/covid19case/fact_epirapo_covid19case.json", {
      await fetch(`https://api.covid19api.com/summary`)
      await fetch(`https://api.covid19api.com/country/${country}/status/${caseType}/live?from=${dateTo}&to=${moment().format('YYYY-MM-DDT00:00:00')}`)
        .then(res => res.json())
        .then(resp => setCovid(resp))
        .catch((err) => { throw (err) })
      console.log("getData ", covid);
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    getData();
  }, [])

  useEffect(() => {
    console.log("Covid : ", covid)
  }, [covid])

  return (
    <div className="main-container">
      {
        covid && (
          <div className="city">
            <div className="city-temp">
              Today
            </div>
            <div className="info">
              <p style={{ color: "black" }}>{covid.label}</p>
            </div>
          </div>
        )
      }
    </div>
  )
}
